void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 screenPos = fragCoord.xy / iResolution.xy; // position in the screen
    vec2 mouse = iMouse.xy / iResolution.xy; // mouse position relative to the screen
    vec4 channel0 = texture(iChannel0, screenPos + vec2(sin(iTime*3.0)*0.5,0.0)); // iChannel0
    vec4 channel1 = texture(iChannel1, screenPos); // iChannel1
    vec4 channel2 = texture(iChannel2, screenPos); // iChannel2
    vec4 channel3 = texture(iChannel3, screenPos); // iChannel3

    if(screenPos.x>.6)
    {
        channel0 = texture(iChannel0, vec2(1.0,0.75)-screenPos);
    }
    screenPos = ceil(screenPos*80.0)/160.0;

    vec4 color = channel1*400.0;

    color += sin(channel0.r*sin(iTime*3.0)*10.0)*40.0*pow(2.0,8.8*distance(mouse,screenPos));

    if(color.g > 0.8)
    {
        color.r -= 2.0*sin(distance(screenPos,vec2(0.7,0.6)))*iTime;
    }
    color.b *= 5.0*sin(distance(mouse,screenPos)*60000.0*iTime);
    color.g = mix(channel0.b,channel1.g,sin(iTime*8.0)/10.0) * sqrt(iTime);

    fragColor = color;
}
