float combine(float a,float b) {
    return (a+b)-(a*b*2.);
}

float circle( vec2 uv, vec2 pos, float size ) {
    return 1.-clamp((length(uv-pos)-size)*1100.,0.,1.);
}

float left_circle( vec2 uv, vec2 pos, float size ) {
    return 1.-clamp((length(uv-vec2(pos.x+(size/2.),pos.y))-(size/2.))*1100.,0.,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float time = iTime;
	vec2 uv = (fragCoord.xy / iResolution.xy);
    vec2 suv = vec2(((uv.x-0.5)*(iResolution.x / iResolution.y))+0.5,uv.y);

    float drawing = 0.;
    vec2 cent = vec2(0.5);
    #define areps 100.
    for (float i = 0.; i < areps; i++) {
        //floor(log2(1/(1-x)))
        //drawing = combine(  circle(uv,vec2(cent.x-(i/areps),cent.y),0.5-(i/areps))  , drawing);
        drawing = combine(  left_circle(suv,vec2(0.,cent.y), (2./(i*2.)) ) , drawing);
    }

	fragColor = vec4(fract(1./(suv.x*2.))/2.,drawing,0.,1.0);
    fragColor *= 0.7;
}
