#define tex2d texture
#define LINEAR_STEPS 20.0
#define DISTANCE 0.16
#define FEATURES 0.1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 dir = vec2(uv - vec2(.5, .5)) * (DISTANCE / LINEAR_STEPS);
    uv += iTime * vec2(0, 0.3);

    for (float i = 0.01; i < LINEAR_STEPS; i++) {
        if (
            	pow(length(tex2d(iChannel0, uv - i * dir).rgb) / 1.4, 0.20) * (1.0 - FEATURES) +
                pow(length(tex2d(iChannel1, (uv - i * dir) * 2.0).rgb) / 1.4, 0.90) * FEATURES
            	 > i / (LINEAR_STEPS)) {
        	fragColor = tex2d(iChannel0, uv - i * dir) * i / (LINEAR_STEPS);
        }
    }
}
