vec4 rect(vec2 uv, vec2 pos, float r)
{
    vec2 reC = abs(uv - pos);
    vec2 dif1 = reC-r/2.;
    vec2 dif2 = clamp(reC - r/2., 0.0, 1.);
    float d1 = clamp(dif1.x + dif1.y, 0.0, 1.0);
    float d2 = clamp(dif2.x + dif2.y, 0.0, 1.0);

    return vec4(d1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;
    float t = sin(iTime);


    vec2 c = iResolution.xy * 0.5;// + sin(iTime) * 50.;

    uv = mat2(cos(t), -sin(t), sin(t), cos(t)) * (uv - c) + c;


	fragColor = rect(uv, c, sin(iTime * 10.) * 50. + 50.);
    fragColor *= vec4(vec3(0.5, 0.2, 1.), 1.);
    fragColor += rect(uv, c, sin(iTime) * 50. + 50.);
    fragColor *= vec4(vec3(0.5, 0.8, 1.), 1.);
}
