vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 color1 = vec3(1, 0, 0);
	vec3 color2 = vec3(1, 1, 1);
	vec3 pixel;

	vec2 p = fragCoord.xy / iResolution.xy;
    vec2 q = -1.0 + 2.0*p;
    q.x *= 1.777; 

    vec2 modu = floor(mod(fragCoord.xy, vec2(50.0, 50.0)));
    if(modu.x < 25.0 && modu.y >= 25.0){
    	vec3 hsv = rgb2hsv(color1);
    	hsv.x = (0.6*p.y+cos(iTime*0.2));
        hsv.y = 0.6;
    	pixel = hsv2rgb(hsv);
    }else if(modu.y < 25.0 && modu.x >=25.0 ){
        vec3 hsv = rgb2hsv(color1);
    	hsv.x = (0.6*p.x+sin(iTime*0.2));
        hsv.y = 0.6;
   	 	pixel = hsv2rgb(hsv);
    }else{
        vec3 hsv = rgb2hsv(color2);
        hsv.z = clamp(sin(iTime*0.2),0.0,0.5);
    	pixel = hsv2rgb(hsv);
    }



	fragColor = vec4(pixel, 1.0);
}
