void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy-.7)*4.;

    int n = int((iMouse.y/iResolution.y)*14.);

    vec2 q = uv;
    float d=999.,dq = 1.;
    for (int i = 0; i < 14; i++) {
        q = q.yx + cos((uv-q)*d)*2.;
        dq = abs(dot(q,uv.yx));
        d = min(d,dq);

        if (i > n) break;
    }

	fragColor = vec4(d);
}
