void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float width = iResolution.x;
    float height = iResolution.y;
    float t = iTime;
    float x = fragCoord.x-width/2.0;
    float y = fragCoord.y-height/2.0;

    float p = 0.20;
    float d = pow(x*x+y*y, 0.25)*1.0/p+t*4.0;

    float b = sin(d)*2.0;
    fragColor = vec4(b, b, b, 1.0);
}
