void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    //The colors
    vec4 white = vec4(1.0, 1.0, 1.0, 1.0);
    vec4 black = vec4(0.0, 0.0, 0.0, 1.0);

    // you can change the number of rows or columns here
    const int numRow = 5;
    const int numCol = 5;

    for (int i = 0; i < numRow; i++)
    {
        for (int j = 0; j < numCol; j++)
        {
           float x = float(i);
           float y = float(j);

           // values for the boundaries
           float leftBound = 1.0 * (x / float(numRow));
           float rightBound = 1.0 * (x / float(numRow)) + 1.0 / float(numRow);
           float topBound = 1.0 * (y / float(numCol));
           float bottomBound = 1.0 * (y / float(numCol)) + 1.0 / float(numCol);

           // this is where it determines the color that goes into each space
           if (uv.x > leftBound && uv.x < rightBound)
            {
                if (uv.y > topBound && uv.y < bottomBound)
                {
                    if (mod(float(i), float(2)) == 0.0)
                    {
                        if (mod(float(j), float(2)) == 0.0)
                        {
                            fragColor = white;
                            break;
                        }
                        else
                        {
                            fragColor = black;
                            break;
                        }
                    }
                    else
                    {
                        if (mod(float(j), float(2)) == 0.0)
                        {
                            fragColor = black;
                            break;
                        }
                        else
                        {
                            fragColor = white;
                            break;
                        }
                    }
                }
            }
        }
    }
}
