const vec3 backgroundColor = vec3(0.0, 0.0, 0.0);
const vec3 RED 	= vec3(1.0, 0.0, 0.0);
const vec3 GREEN 	= vec3(0.0, 1.0, 0.0);
const vec3 BLUE 	= vec3(0.0, 0.0, 1.0);
float radiusCircle = 0.3;

vec3 Circle(vec2 uv) {

    float d = length(uv); // length(uv) == sqrt(uv.x*uv.x + uv.y*uv.y)
    vec3 color = backgroundColor;

    if(d <= radiusCircle) 
        color = GREEN;

    return color;
}

vec3 Rectangle(vec2 uv, vec3 colorRectangle, bool position) {

    float d = uv.x;
    vec3 color = backgroundColor;
    float medium = 0.0;

    if(!position)
    {
		if(d < medium && length(uv) > radiusCircle) 
        	color = colorRectangle;
    }
    else
    {
        if(d > medium && length(uv) > radiusCircle) 
        	color = colorRectangle;
    }

    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ( fragCoord.xy / iResolution.xy ) - 0.5;  // -0.5 <> 0.5
    uv.x *= iResolution.x / iResolution.y;

 	bool Left = false; 
    bool Right = true; 

    vec3 c = backgroundColor;

    c += Circle(uv);
    c += Rectangle(uv, RED, Left);
    c += Rectangle(uv, BLUE, Right);


    fragColor += vec4(c, 1.0);
}
