void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 xy = fragCoord.xy/iResolution.xy;
    vec4 text = texture (iChannel0,xy);


     // by using addition and then larger division, 
    // you can create a smoother gradient and
    // maintain a bright image

    text.r+= 0.03 +(xy.y+xy.x)/10.0; // if x and y are both high, rgb should be 1 (bright)
    text.g+= 0.03 +(xy.y+xy.x)/10.0;
    text.b+= 0.03 +(xy.y+xy.x)/10.0;

 /*   
    text.r+= 0.1+(xy.y-xy.x)/7.0; // if y high and x low, rgb should be 1 (bright)
    text.g+= 0.1+(xy.y-xy.x)/7.0; 
    text.b+= 0.1+(xy.y-xy.x)/7.0;
   */ 

    fragColor = text;


}
