void mainImage(out vec4 c, vec2 x) {
	vec2 i = iResolution.xy;
	vec2 p = x / i;
	p = (x * 2. - i) / i.y * (p.x * p.y * .8 + .6);

	float t = iTime * .2;
	t = length(p) * 5.
		+ sin(t * .7 - sin((p.x * cos(t) + p.y * sin(t)) * 2. - t * 4.) * 2. + p.y * .4)
		+ sin(sin(t * 4. - (p.y * cos(t) - p.x * sin(t)) * 3.) + t + p.x * .3)
	;

	c = vec4(sin(t * 32.));
}
