float basis(float x) {
  return sin(x) * sin(x);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = fragCoord.xy / iResolution.xy;
  uv.x *= iResolution.x / iResolution.y;

  float x = uv.x * 6.28 * 2.0;
  float t = basis(x) * 0.75;
  float t2 = basis(t * 8.0 + iTime);
  t += t2 * 1.0 / 8.0;

  vec3 c = vec3(0.0);

  float sineTransition = smoothstep(uv.y, uv.y + pow(uv.x, 2.0), t);

  c.r = sineTransition * t2;
  c.g = c.r * 0.1;
  c.b = c.r * 0.4;

  c.rgb += (1.0 - sineTransition) * (1.0 - t2);

  fragColor = vec4(c, 1.0);
}
