float linearForm(vec2 p1, vec2 p2, float x)
{
	float m = (p2.y - p1.y) / (p2.x - p1.x);
    float b = p2.y - (m * p2.x);
    return (m * x) + b;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;
	vec2 center = vec2((iResolution.x / iResolution.y) * 0.5, 0.5);

    vec2 delta = uv - center;
    float angle = (atan(delta.y, delta.x) + 3.141592) / (2.0 * 3.1415);
    angle = angle * angle * angle;
    float mag = length(delta);


    float squeeze = 0.9;
    float minSize = 0.014 * squeeze;
    float maxSize = 0.025 * squeeze;
    float radius = 0.4;

    float mySize = linearForm(vec2(0, minSize), vec2(1, maxSize), angle);

    float wrongness = abs(mag - radius);


    float boundness = (mySize - wrongness) * iResolution.y;

    fragColor = vec4(vec3(max(boundness * 0.5, 0.0)), 1.0);

}
