void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord / (iChannelResolution[0].xy * 32.0);
    float offset = sin(iTime / 1.) * cos(iTime / 1.) * .1;
    vec4 texColor = texture(iChannel0, coord);
    vec4 texColor2 = texture(iChannel0, vec2(coord.x, coord.y + offset));
    vec4 texColor3 = texture(iChannel0, vec2(coord.x + offset, coord.y));
    fragColor = vec4(texColor.r, texColor2.g, texColor3.b, 1.);
}
