#define SPEED 0.3

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 r =  2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
    float xMax = iResolution.x/iResolution.y;

    vec3 col1 = vec3(0.3137254902, 0.9568627451,1.0); // blue
    vec3 col2 = vec3(1.00, 0.329, 0.298); // yellow
    vec3 col3 = vec3(0.867, 0.910, 0.247); // red

    vec3 pixel = vec3(0.,0.,0.);


    // set background
    float xVal = r.x;
    for(int i = 0; i < 20; i++)
    {
        if(xVal > xMax - 0.1 && xVal < xMax)
        {
            pixel = vec3(1.);
        }
        xVal += 0.2;
    }
    if(iMouse.z > 0.)
    {
        pixel = vec3(0.5);
    }

    float lengthOfBoxes = 0.4;
    float heightOfBoxes = 0.2;
    float centerOfBoxes = mod(iTime*SPEED, xMax*2.0) - xMax;

    //set yellow box moving to the right
    if(r.y > 0.2 && r.y < 0.2 + heightOfBoxes)
    {
        if(r.x > centerOfBoxes - lengthOfBoxes/2. && r.x < centerOfBoxes + lengthOfBoxes/2.)
        {
			pixel = vec3(1.,1.,0.);
        }
    }

	//set blue box moving to the right
    if(r.y < -0.2 && r.y > -0.2 - heightOfBoxes)
    {
		if(r.x > centerOfBoxes - lengthOfBoxes/2. && r.x < centerOfBoxes + lengthOfBoxes/2.)
        {
			pixel = vec3(0.,0.,1.);
        }
    }

    fragColor = vec4(pixel, 1.0);
}
