void mainImage( out vec4 o, vec2 p )
{
    p = (p+p-(o.xy=iResolution.xy))/o.y/.7;
    vec2 z = p;
    float a, r, d = 2.+2.*(1.-cos(iTime/5.));
    o = vec4(0,0,0,1);
    for( float i = 0. ; i < 100. ; i++ ) {
        r = length(z);
        a = d *.5* atan(z.y, z.x);
        z = pow(r, d) * vec2(cos(a), sin(a)) + p;
        if( r > 100.) {
            o.r = d * i/100.;
            break;
        }
    }
    o = sqrt(o);
}
