#define S smoothstep
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 5. * (fragCoord - .5 * iResolution.xy) / iResolution.x;

    float r = length(uv);
    float a = atan(uv.y, uv.x) - iTime*1.5;

    float b = fract((log(abs(log(r)))*12.+a)/6.28);
    float db = fwidth(b)*1.5;
    fragColor = vec4(vec3(S(.25+db, .25, b)*S(db, db+db, b)), 1.);

    float cw = 1./3.;
    float c = mod((r+a)/6.28, cw);
    float dc = fwidth(r)*1.5;
    fragColor.rgb *= 1. - S(0., cw, c)*S(cw-dc, cw-2.*dc, c);
}
