vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 tc = uv;
    float aspect = iResolution.x / iResolution.y;

    uv = uv * 2.0 - 1.0;
    uv.x *= aspect;

   	uv += vec2(sin(iTime) , cos(iTime))*0.025;

    float s = 1.0 - smoothstep(0.0, 0.6, length(max(abs(uv.x), abs(uv.y)) ));
    s = sin(s);

    tc = 2.0 * tc - 1.0;
    tc *= 0.999;
    tc = tc * 0.5 + 0.5;

    vec3 h = texture(iChannel0, tc).rgb;
    h = rgb2hsv(h);
    h.r += iTime*0.1;

    vec2 texel = 1.0 / iResolution.xy;
    vec2 d = texel*0.75;
    vec3 p = texture(iChannel0, tc + vec2(sin(h.r*6.283),cos(h.r*6.283))*d ).rgb;


    vec3 c = vec3(s,0.0,0.0) + (p);

    c = clamp(c, vec3(0.0), vec3(1.0));

    c = rgb2hsv(c);
    c.r += 0.002;

    c = hsv2rgb(c);
    c = c.r > 0.99 ? fract(c.rgb) : c;

    fragColor = vec4(c , 1.0);
}
