void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    /*
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
	*/
    //vec2 p1 = vec2 (50, 50);
    /*vec2 p1 = iMouse.xy;
   	vec2 p2 = vec2 (300, 80);*/
    vec2 p1 = vec2 (.5 + .3 * cos (iTime * .5), .5 + .3 * sin (iTime * .7)) * iResolution.xy;
   	vec2 p2 = vec2 (.5 + .2 * cos (iTime * 1.1), .5 + .2 * sin (iTime * .9)) * iResolution.xy;

    vec4 colA = vec4(clamp (5.0 - length (fragCoord - p1), 0.0, 1.0));
    vec4 colB = vec4(clamp (5.0 - length (fragCoord - p2), 0.0, 1.0));

    vec2 a_p1 = fragCoord - p1;
    vec2 p2_p1 = p2 - p1;
    float h = clamp (dot (a_p1, p2_p1) / dot (p2_p1, p2_p1), 0.0, 1.0);
    float d = length (a_p1 - p2_p1 * h);

    vec4 colC = clamp (vec4(.5 - 5.0 * cos ((d - iTime * 10.0) * 3.1415926 / 20.0)), 0.0, 1.0) * vec4 (1.0, .73, .0, .0);
    vec4 colD = vec4(clamp (2.5 - d, 0.0, 1.0));

    fragColor = max (max (colA, colB), max (colC, colD));
}
