float rand(vec2 uv){
    if (iMouse.x > 0. || iMouse.y > 0.){
    	return fract(sin(dot(uv.xy, vec2(iMouse.y/1000., iMouse.x/100.)))*43758.5453123);
    }else{
        return fract(sin(dot(uv.xy, vec2(.005, .0034)))*43758.5453123);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	float x = rand(uv*fract(iTime/100.));
    fragColor = vec4(1.-x,1.-x,1.-x,1.0);
}
