#define CAM_COMPENSATION_X 0.8

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(
        fragCoord.x / iResolution.x,
        fragCoord.y / iResolution.y * 
        	CAM_COMPENSATION_X + (1. - CAM_COMPENSATION_X) / 2.
    );
    uv.x = 1. - uv.x;

    fragColor = texture(iChannel0, uv);
}

#define TRAIL_FACT 50.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    vec4 last = texture(iChannel1, uv);
    vec4 cam = texture(iChannel0, uv);


    fragColor = (cam + last * TRAIL_FACT) / (TRAIL_FACT + 1.);
}
