#define N 16
#define fN float(N)

void mainImage( out vec4 O, vec2 U )
{
    float t = floor(iTime*60./4.); 
    U /= iResolution.xy;    
    float m = iMouse.x / iResolution.x;

    if ( U.x > 2.*(t-45.)/(fN*fN) ) { // loading phase
        O = abs(U.y-.5)<.1 ? vec4(1) : texture(iChannel0, U); 
        return; }

    t = -t;
    if (length(m)>.01) t=floor(m*fN*fN);
    vec2  V = mod(vec2(t,floor(t/fN)),fN);   

    O = sqrt(texture(iChannel0, fract((V+U)/fN)));

	//O = texture(iChannel0, U);    
}

#define N 16
#define fN float(N)

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
	O = texture(iChannel0, U/R);
    int t = (iFrame-180)/4;            // sample only 1 every 4 frames
    vec2 V = vec2(t-t/N*N,t/N);        // image index (t)
    U = U/R*fN;
    if (floor(U)==V)  
	   O = texture(iChannel1, fract(U));
}
