float pi = 3.1415;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = abs(fragCoord.xy / iResolution.xy - 0.5);
    float wallexpansion = ((sin(iTime) * 0.3 + 0.7) *20.0);
    float vwall = (cos(1.0 * pi / uv.y + iTime * 8.0));

    vec2 av = vec2(uv.y, 1.0 - abs(cos((uv.x * wallexpansion / uv.y)) * vwall));


	fragColor = (1.0 - dot(av, uv)) * vec4(.6,.0,.2,1);
}
