// taken from https://www.shadertoy.com/view/4d2GDw


vec3 view(vec2 p)
{
	float r = iResolution.y / iResolution.x;
	float x = p.x-fract(p.x*80.)/80.;
	float h = texture(iChannel0,vec2(x,0)).x;

	if (p.y <= h-fract(h*80.*r)/(80.*r)) {
		float py = p.y - fract(p.y*80.*r)/(80.*r);
		return vec3(h,h,py)*1.5;
	}
	return vec3(0.,0.,0.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy/iResolution.xy;
	fragColor = vec4(view(p), 1.);
}
