// Tutorial bÃ¡sico sobre raymarching.
// por Vinicius Graciano Santos (vgs).

// Constants.
#define STEPS 64
#define EPS 0.01
#define FAR 10.0
#define PI 3.14159265359

// Distance Function.
float map(vec3 p) {
    float v = 2.5*iTime;
    float r = 1.35 + 0.15*cos(10.0*p.y + v) + 0.15*cos(10.0*p.x + v);
    return length(p) - r;
}

// The Gradient (number) in the distance function.
vec3 grad(vec3 p) {
    vec2 q = vec2(0.0, EPS);
    return vec3(map(p + q.yxx) - map(p - q.yxx),
                map(p + q.xyx) - map(p - q.xyx),
                map(p + q.xxy) - map(p - q.xxy));
}

vec3 shade(vec3 ro, vec3 rd, float t) {
	vec3 n = normalize(grad(ro + t*rd));
    return vec3(0.3, 0.5, 0.7)*pow(1.0-dot(-rd, n), 1.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Prepare the ratio.
	vec2 uv = (-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;

    vec3 ro = vec3(0.0, 0.0, 2.5); // something? of the radius.
    vec3 rd = normalize(vec3(uv, -1.0)); // direction of the radius.

    // Loop of the raymarcher.
    float t = 0.0, d = EPS;
    for (int i = 0; i < STEPS; ++i) {
        d = map(ro + t*rd);
        if (d < EPS || t > FAR) break;
        t += d;
    }

    // Shading.
    vec3 col = d < EPS ? shade(ro, rd, t) : mix(vec3(0.0), vec3(0.3, 0.5, 0.7), 2.0-length(uv)) ;

    // proccessor. 
    col = smoothstep(0.0, 1.0, col);
    col = pow(col, vec3(0.45));

	fragColor = vec4(col,1.0);
}
