void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    // 0.4 or 0.5 or 0.6
    float x = floor(mix(0.4, 0.699999, uv.x) * 10.0) / 10.0;

    //  0.0 or 0.25, or 0.5, or 0.75
    float y = floor(mix(0.0, 0.999999, 1.0 - uv.y) * 4.0) / 4.0;

    vec4 v1;
    float edge = 0.5;

    // 1.0 if x >= edge, else 0.0
    v1.x = step(edge, x);

    // 1.0 if x <= edge, else 0.0
    v1.y = step(x, edge);

    // 1.0 if x > edge, else 0.0
    v1.z =  1.0 - step(x, edge);

    // 1.0 if x < edge, else 0.0
    v1.w =  1.0 - step(edge, x);    

    vec4 v2 = 1.0 - abs(sign(vec4(y) - vec4(0.0, 0.25, 0.5, 0.75)));

	fragColor = vec4(vec3(dot(v1, v2)), 1.0);
}

