void mainImage( out vec4 color, in vec2 pixCoords )
{
	color = vec4(1.0, 1.0, 1.0, 1.0);
    float distance = sqrt(pow(pixCoords.x - iResolution.x, 2.0) + cos(2.0 * 3.14 / 180.0 * pixCoords.x * iTime) + pow(pixCoords.y - iResolution.y / 2.0, 2.0) + sin(2.0 * 3.14 / 180.0 * pixCoords.y * iTime));

    if (pixCoords.y <  6.0 * cos(2.0 * 3.14 / 180.0 * pixCoords.x + 3.0*iTime) + iResolution.y / 3.0){
        color = vec4(0.0, 0.0, 1.0, 1.0);
    }
    if (distance < pixCoords.y / 4.0){
        if (distance < pixCoords.x / 2.0){
        		color = vec4(0.0, 0.0, 0.0, 1.0);
        }
    }
}
