void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uva = (fragCoord.xy+3.*sin(iTime*vec2(.42,.51)+fragCoord.xy/270.*6.).xy) / iResolution.xy;
	vec2 uvb = (fragCoord.xy+3.*sin(iTime*vec2(.462,.391)+fragCoord.xy/280.*6.).yx) / iResolution.xy;
	fragColor = min(texture(iChannel0, uva),texture(iChannel0, uvb));
}
