void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 z = vec2(0);
    vec2 z_next;

    vec2 a = vec2(0.76 , 0.5);
    uv -= a;
    uv /=((iTime*iTime)/10.0 + 0.3);
    uv+=a;
    vec2 com = (uv - vec2(0.5 , 0.5));

    int isInner = 1;
    float po;
    for(int i=0;i<60;i++){
        z_next.x = z.x*z.x -z.y*z.y + com.x;
        z_next.y = 2.0 * z.x *z.y + com.y;
        z = z_next;
        if(length(z)>2.0){
   			po = float(i)/60.0;
            isInner = 0;
            break;
        }
    }

     vec3 col;
    if(isInner == 0){

        col = vec3(5.0*po+0.2,po,2.5*po+0.3);
    }else{
        col = vec3(1,0.9,1);
        }
    //col = vec3(length(z));


    fragColor = vec4(col,1.0);
}
