void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 color = vec4(1.0,1.0,1.0,1.0); // final color, initial value is white

    vec2 screenPos = fragCoord.xy / iResolution.xy; // position in the screen
    vec2 mouse = iMouse.xy / iResolution.xy; // mouse position relative to the screen
    vec4 channel0 = texture(iChannel0, screenPos); // iChannel0
    vec4 channel1 = texture(iChannel1, screenPos); // iChannel1
    vec4 channel2 = texture(iChannel2, screenPos); // iChannel2
    vec4 channel3 = texture(iChannel3, screenPos); // iChannel3

    color.r = sin(channel1.r*iTime) + log(atan(iTime)-log(cos(iTime)));
    color.g = fract(cos(pow(channel3.r*1.5, sqrt(iTime)))-atan(channel3.r*0.7));
    color.b = (channel1.g+atan(channel0.r));
    color.a = channel2.r;

    color.rb += fract(cos(pow(channel3.r*1.5, sqrt(iTime)))-atan(channel3.r*4.25))-0.7;

    if((fragCoord.x/iResolution.x > 0.7 && fragCoord.x/iResolution.x < 0.9) && fragCoord.y/iResolution.y <= 0.725)
    {
    	color -= 1.0;
    }
    if((fragCoord.x/iResolution.x > 0.1 && fragCoord.x/iResolution.x < 0.3) && fragCoord.y/iResolution.y >= 0.275)
    {
    	color -= 0.6;
    }

    if(sin(iTime) < 0.2 || sin(iTime) > 0.9)
    {
    	color = sin(color*iTime);
        if(tan(iTime) < 0.1)
        {
        	color.r = sqrt(color.g);
        }
        else if(tan(iTime) > 0.87)
        {
            color.bg = sqrt(color.rb);
        }
    }

   if((fragCoord.x/iResolution.x > cos(iTime)-1.0 && fragCoord.x/iResolution.x < tan(iTime)*1.3) && fragCoord.y/iResolution.y <= cos(iTime)*0.7)
    {
    	color.r -= 1.0;
        color.b *= 2.0*sin(iTime)*tan(iTime + fragCoord.x/iResolution.y);
        color.g = abs(sqrt(color.g)-sin(iTime));
    }

    clamp(color, atan(iTime), 9999999.0);

	fragColor = color;
}

vec2 mainSound( in int samp,float time)
{
    vec2 sound = vec2( sin(6.2831*440.0*time)*exp(-3.0*time) );

    sound -= atan(time);
 	min(sound, 0.5);
    max(sound, 0.6);


    return sound;
}
