// [2TC 15] Monochrome atan() - K*log()
// Copyleft {c} 2015 Michael Pohoreski
// Chars: 180

// Chars: 149
// Linear:     fragColor = vec4( fract( atan(p.y,p.x) - t*t*log(length(p)) ) > .5 );

// Pattern stops, then reverses at:
// 2PI*1440 =(t/2)^2
// t = 2*18.973...
//   = 37.94
/* Original
void mainImage( out vec4 f, in vec2 w )
{
    vec2 p = 2.*w/iResolution.xy - 1.;
    float t = iTime*0.5;
    f = vec4( fract( atan(p.y,p.x) - 1440.*(sin(6.28318530718*t*t/1440.))*log(length(p)) ) > .5 );
}
*/

// Minified version by Greg Rostami -- Thanks!
void mainImage( out vec4 f, vec2 w )
{
    w = w/iResolution.xy-.5;
    float t = iTime,c=1440.;
    f = vec4( fract( atan(w.y,w.x) - c*sin(6.28*t*t/c)*log(length(w)) ) > .5 );
}
