vec4 circle(vec2 xy,  vec3 color) {
    vec2 pos = iResolution.xy * 0.5;
    float radius = 0.3 * iResolution.y;
	float d = length(pos - xy) - radius;
	float t = clamp(d, 0.0, 1.0);

    xy.x = xy.x / iResolution.x; //We divide the coordinates by the screen size
   	xy.y = xy.y / iResolution.y;

    color.b = xy.x;
    color.g = xy.y;

   	color.b *= abs(cos(iTime));
	return vec4(color, 1.0 - t);
}


vec4 background(vec2 xy, vec4 color){

    xy.x = xy.x / iResolution.x; //We divide the coordinates by the screen size
   	xy.y = xy.y / iResolution.y;

    color.r = xy.y;

    return vec4(color);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy; //We obtain our coordinates for the current pixel

      // Background layer
	vec4 layer1Color = vec4(1.0, 1.0, 1.0, 1.0);


	// Circle
	vec3 green = vec3(0.0, 1.0, 0.0);
    green.g *= abs(sin(iTime) * cos(iTime));

	vec4 layer2 = circle(xy, green);
	vec4 layer1 = background(xy, layer1Color); 

	fragColor = mix(layer1, layer2, layer2.a);

}
