float angle(vec2 coord, vec2 center){
	float dx = coord.x - center.x;
    float dy = coord.y - center.x;
    return atan(dx/dy);
}

float diric (float n, float x);
vec3 hsl2rgb (float h, float s, float l);

vec3 hsl(in float h, float s, float l) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  return l + s * (rgb-0.5)*(1.0-abs(2.0*l-1.0));
}

float amod(float c, float r, float f, float t){
	return (c + (sin(t * f * 6.282) * r));
}

float ii = 0.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    ii = ii + 1.1;
    float t = iTime * 0.0005 + (200.0 + ii);


    float w = iResolution.x - (15.0 * cos(iTime * 2.0));
    float h = iResolution.y + (5.0 * cos(iTime * 3.0));

    float x = (fragCoord.x - w / 2.0) * amod(0.0, 3000000.0, t, .000125);
    float y = (fragCoord.y - h /  2.0) * amod(0.0, amod(0.0001, amod(.05, .04, t, 72.0/600.0*1.0/4.0), t, .0025), t, 125.0);
    float xx = mod(x, 20.0);
    float yy = mod(y, 20.0);
    vec2 c = vec2( w / 2.0, h / 2.0);
    float a = angle(vec2(xx, yy), c);
    float vv = sin(t* y * x + log(t)) + sin(a - 0.5 * 72.0 / 60.0);
    vec3 uv2 = hsl(1.0 - mod(vv, amod(.6, .4, t,1.0/24.0)), 1.0, .5);
	fragColor = vec4(uv2, 1.0);

}
