#define M_PI 3.14159265358979323846

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 size = vec2(1.0, iResolution.y / iResolution.x);
	vec2 uv = fragCoord.xy / iResolution.xy * size;

    vec2 centre = size / 2.0;

    vec2 dist = uv - centre;

    float dist_rad = sqrt(sqrt(pow(dist.x, 2.0) + pow(dist.y, 2.0))) * M_PI * 3.0;

    float intensity = (sin(dist_rad * 13.0 + iTime * 12.0) + 1.0) / 2.0;

    float polar = atan(dist.x, dist.y) + iTime;

    float intensity_2 = pow(sin(polar * 10.0 - dist_rad * 13.0), 2.0);

    float intensity_3 = clamp(dist_rad / 2.0, 0.0, 1.0);

	vec4 final = vec4(uv,0.5+0.5*sin(iTime),1.0) * intensity * intensity_2 * intensity_3; 

    fragColor = final;
}
