void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy);
    fragColor = texture(iChannel0, uv);
}

const float circleRadius = 0.1;
const float fade = 0.99;

float drawCircle(vec2 pos, vec2 uv) {
    float dist = distance(pos, uv);
    if(dist < circleRadius) {
        float a = 1.0;
        // Anti-alias
        float maxDist = circleRadius * 0.6;
        if(dist > maxDist) {
            a = 1.0 - ((dist - maxDist) / (circleRadius - maxDist));
        }
        return a;
    }
    return 0.0;
}

vec3 HueShift (in vec3 Color, in float Shift)
{
    vec3 P = vec3(0.55735)*dot(vec3(0.55735),Color);

    vec3 U = Color-P;

    vec3 V = cross(vec3(0.55735),U);    

    Color = U*cos(Shift*6.2832) + V*sin(Shift*6.2832) + P;

    return vec3(Color);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy);
    // Correct positioning
    vec2 aspect = vec2(iResolution.x / iResolution.y, 1.0);

    // Draw previous buffer (with slight blur via LOD)
    vec4 color = texture(iChannel0, uv, 1.5);
    // Fade previous image
    color.xyz -= vec3(1.0 - fade);

    // Draw new circle
    vec2 pos = vec2(
        sin(iTime) * 0.3 + 0.5,
        cos(iTime) * 0.3 + 0.5
    );
    if(iMouse.z > 0.0) {
        pos = iMouse.xy / iResolution.xy;
    }
    float circle = drawCircle(pos * aspect, uv * aspect);
    color.rgb = mix(color.rgb, HueShift(vec3(uv, 1.0), iTime * 0.1), circle);
    fragColor = color;
}
