void mainImage(out vec4 out_col, vec2 coord) {
    vec2 uv = coord / iResolution.xy;

    float vig = distance(uv, vec2(0.5));

    vec3 bg_col = mix(vec3(0.0, 0.6, 1.0), vec3(0.0, 0.2, 0.4), uv.y - vig);
    vec3 fg_col = mix(vec3(0.0, 0.4, 0.0), vec3(0.5, 1.0, 0.5), uv.y - vig);

    float factor = sin(coord.x + iTime);
    factor *= sin(coord.y + iTime);

    vec3 col = uv.y > factor ? bg_col : fg_col;

    out_col = vec4(col, 1.0);
}
