#define BASE ceil(iTime)
const float x = 1.+sqrt(5.)/2.;
//const float x = acos(-1.);
//const float x = sqrt(2.);

vec2 cpow(vec2 a, float p)
{
    float t = atan(a.y,a.x);
    float l = length(a);
    return pow(l,p)*vec2(cos(p*t),sin(p*t));
}

void mainImage( out vec4 O, vec2 I )
{
	I= 2.*(2.*I- iResolution.xy)/iResolution.x;
	vec2 c = iMouse.x>0.? (2.*iMouse.xy- iResolution.xy)/iResolution.x:I
        ,z=I;
    vec4 dmin=vec4(1e6);
    for(int i =-1;i++<32;)
    {
     float p = BASE*fract(floor(pow(x,float(i)))/BASE);
     z=cpow(z,p)+c;
    		dmin=min(dmin, vec4(abs(length(c)-length(z))
                           ,length(sin(z))*length(c)
                           ,abs(z.x)
                           ,abs(dot(z,c))));
    }    
    vec3 color = vec3( pow(dmin.r,0.6) );
	color = mix( color, vec3(1.00,0.50,0.20), min(1.0,pow(dmin.w*0.5,0.40)) );
    color = mix( color, vec3(0.4,0.70,0.40), min(1.0,pow(dmin.y*.1,0.50)) );
	color = mix( color, vec3(1.00,.70,.50), 1.0-min(1.0,pow(dmin.z*1.00,0.15) ));

    O = vec4(color,1.);
}
