float image(vec2 uv, vec2 size, vec2 pos, float aspectR);

//house stat
vec2 houseSize = vec2(0.2,0.2);
vec2 housePos = vec2(0.5,0.3);
vec4 houseColor = vec4(0.0,0.0,1.0, 1.0);

//chimney stat
vec2 chimneySize = vec2(0.025,0.07);
vec2 chimneyPos = vec2(0.6,0.78);
vec4 chimneyColor = vec4(0.0,1.0,0.0, 1.0);

//window stat
vec2 windowSize = vec2(0.05,0.05);
vec2 windowPos = vec2(0.4,0.3);
vec2 windowPos1 = vec2(0.6,0.3);
vec4 windowColor = vec4(1.0,0.0,0.0, 1.0);

//other
vec4 BG = vec4(1.0,1.0,1.0,1.0);

//toggle
float houseToggle = 1.0;  
float chimneyToggle = 1.0;
float windowToggle = 1.0;

//step process into function to make more readibility and efficiency
float image(vec2 uv, vec2 size, vec2 pos, float aspectR)
{
    pos.y *= aspectR;
  float image = step(length(pos.x-uv.x),size.x) * step(length(pos.y-uv.y),size.y);
  return image;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float aR = iResolution.y / iResolution.x;
    uv.y *= aR;

    vec4 windLay = windowColor * image(uv,windowSize,windowPos, aR) * windowToggle;
    vec4 windLay1 = windowColor * image(uv,windowSize,windowPos1, aR) * windowToggle;

    vec4 windComp = mix(windLay, windLay1, windLay1.a);

    vec4 layer0 = houseColor * image(uv,houseSize,housePos, aR) * houseToggle;
    vec4 layer1 = chimneyColor * image(uv,chimneySize,chimneyPos, aR) * chimneyToggle;
    vec4 layer2 = windComp;

    vec4 composite = mix(BG, layer0, layer0.a);
    composite = mix(composite, layer1,layer1.a);
    composite = mix(composite, layer2, layer2.a);
    fragColor = composite;
}
