//	Created by zomee 2017.

//	noise based on iq's work
float noise(vec2 co)
{
    return fract(sin(dot(vec2(co.x, 1.), vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 tex0 = texture(iChannel0, uv);
    vec4 tex1 = texture(iChannel1, uv);
    vec4 col = vec4(0.);

    float a = noise(uv);
    float blend = sin(iTime * 2.) * .5 + .5 ;
    blend = step(a, blend);

    col = mix(tex0, tex1, blend);

	fragColor = col;
}
