 float lerp(float t, float a, float b){
      return (1.0 - t) * a + t * b;
  }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x = fragCoord.x;
    float y = fragCoord.y;
	float xSize = 30.0 ;
    float ySize = 25.0;
    float maxSize = 10.;
    float reduction = 8.;

    vec2 gridSize = vec2(mod(iResolution.x, xSize), mod(iResolution.y, ySize)) ;

    vec2 pos = vec2(mod(fragCoord.x  + iTime * 30., xSize), mod(fragCoord.y, xSize));



    float distx = distance(pos.x, gridSize.x);
    float disty = distance(pos.y, gridSize.y);

    float progress= x/iResolution.x;

    float col;
    if( progress < 0.5){
        col = lerp(max(clamp(((0.45 - progress)/1.)/(distx/xSize), 0.0, .7), clamp(disty/ySize, 0.0, 1.0)), 0.0, 0.8); 
    } else {
        col = lerp(max(clamp((distx/xSize)- (1.1 - progress * 1.25), 0.0, .8), clamp(disty/ySize, 0.0, 1.0)), 0.0, 0.8); 


    }
	float distFromCenter = abs(x - (iResolution.x/2.))/(iResolution.x/2.);



    if(distx <= maxSize -(reduction * distFromCenter)){
    	col = lerp(1.0- (iResolution.x - fragCoord.x)/iResolution.x, 0.25, 1.0);
    }

    //Intensity increases from left to right
    float colorAdjustment = lerp(progress, -0.2, .3 + (.1 * (sin(iTime) * 0.2)));
    //The effect of the adjustment can be scaled per channel
    float r = 1.0;
    float g = .7;
    float b = .4;
	fragColor = vec4(col + colorAdjustment * r,col + colorAdjustment * g,col + colorAdjustment * b,1.0);
}
