vec2 rotation(in float angle,in vec2 position,in vec2 center)
{
    //Function seen from https://www.shadertoy.com/view/XlsGWf
    float rot = radians(angle);
    mat2 rotation = mat2(cos(rot), -sin(rot), sin(rot), cos(rot));
    return vec2((position-center)*rotation);
}

vec2 scale(vec2 pos, float size, vec2 center)
{
 	return vec2((pos-center)*size);   
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //uv
	vec2 uv = fragCoord.xy / iResolution.xy;
    //Stretched uv
    //vec2 struv = ((uv-0.5)*vec2(1.,0.58))+0.5; (2015-May-2)
    vec2 struv = vec2(((uv.x-.5)/(iResolution.y/iResolution.x))+.5,uv.y);
    //time
    float time = iTime;
    //The rotozoom uv
    vec2 rotozoom = rotation(time*10.,vec2(scale(struv,(abs(sin(time))*4.)+3.,vec2(0.5)))+(vec2(sin(time/2.),cos(time/2.))*4.),vec2(0.5));
    //Make texture
    vec4 texture = texture(iChannel0,fract(rotozoom));
    //Finished
	fragColor = vec4(texture.rgb,1.0);
    //fragColor = vec4(texture.rgb+vec3(fract(rotozoom),0.),1.0);
}
