 void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.y = 1.0-uv.y;
    vec2 neighbor = vec2(1,1)/ iResolution.xy;

    vec4 mainColor = texture( iChannel0, uv );
    vec4 npxColor = texture( iChannel0, uv+vec2( -neighbor.x, -neighbor.y)  );
    npxColor += texture( iChannel0, uv+vec2( -neighbor.x, neighbor.y)  );
    npxColor += texture( iChannel0, uv+vec2( neighbor.x, -neighbor.y)  );
    npxColor += texture( iChannel0, uv+vec2( neighbor.x, neighbor.y)  );
    npxColor /= 4.0;

    float offset = 0.01;

    bool isLessThan = any( lessThan( mainColor+offset, npxColor ) );

	fragColor = (isLessThan ? vec4(0,0,0,1) : vec4(1,1,1,1));
}
