void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float num =50.0;
    float n; 
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 tile = num * uv - floor(num * uv);
    vec4 img = texture(iChannel1, tile);
    float avg = (img.x + img.y + img.z) / 3.0;
    vec2 mosaic = floor(uv * num) / num;
    vec4 mosImg = texture(iChannel0, mosaic);
    float mosAvg = (mosImg.x + mosImg.y + mosImg.z) / 3.0;
    if(mosAvg > avg)
        n = 1.;
    else
        n = 0.5;
    if(n< 1.)
        fragColor = vec4(n+ sin(iTime), n + .1, n + cos(exp(20.)), 1.0);
    else
		fragColor = vec4(n, n, n, 1.0);
}
