float mic(float x, float y){
 return texture(iChannel0, vec2(abs(mod(x+1., 2.)-.1),y)).r;   
}

float com(vec2 uv, float i){
    float d = length(uv)*.1;

    //uv*= mat2(d,sin(i+d),sin(-i+d),d);
    //uv*= mat2(0,sin(i+d),sin(-i+d),0);
    //uv*= mat2(1,sin(i+d),sin(-i+d),1);
    //uv*= mat2(sin(i-d),0,0,sin(i-d));
    //uv*= mat2(tan(d+1.5),sin(i),cos(i),tan(d+1.5));
    //uv*= mat2(cos(i),sin(d),cos(d),sin(i));
    //uv*= mat2(cos(i),d,d,sin(i));
    //uv*= mat2(sin(i+d),cos(i+sin(i+d)),sin(i+cos(i-d)),cos(d));
    uv*= mat2(d+mic(d, .3),cos(i+sin(i+d)),sin(i+cos(i-d)),d);

    float v =  floor(mod(uv.y,2.0));
    return floor(mod(uv.x+v,2.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float i = iTime*.333;

    fragCoord.xy -= iResolution.xy*.5;
	vec2 uv = fragCoord.xy / iResolution.xx;
    uv*=.4+mic(.1,.3)*0.1;
    float o = sin(i)+1.;
    float f = com(uv*20.,i);
    f = max(f,com(uv*(20.+o),i)*.6);
    f = max(f,com(uv*(20.+o*2.),i)*.3);

	fragColor = vec4(f,f,f,1.0); 
}
