// ALL TAKEN FROM IQs AMAZING SITE / TUTORIALS / SHADERS:
// https://iquilezles.org/www/index.htm
// https://www.shadertoy.com/user/iq


const float MAX_TRACE_DISTANCE = 10.0;           // max trace distance
const float INTERSECTION_PRECISION = 0.001;        // precision of the intersection
const int NUM_OF_TRACE_STEPS = 200;
const float PI = 3.14159;


// iq's noise
float pn( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);
	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
	vec2 rg = textureLod( iChannel0, (uv+ 0.5)/256.0, 0.0 ).yx;
	return -1.0+2.4*mix( rg.x, rg.y, f.z );
}


float fpn(vec3 p) {
   return pn(p*.06125)*.5 + pn(p*.125)*.25 + pn(p*.25)*.125;
}

vec3 hsv(float h, float s, float v)
{
  return mix( vec3( 1.0 ), clamp( ( abs( fract(
    h + vec3( 3.0, 2.0, 1.0 ) / 3.0 ) * 6.0 - 3.0 ) - 1.0 ), 0.0, 1.0 ), s ) * v;
}


// q is point
// n is normal
// p is point on plane
vec3 projOnPlane( vec3 q, vec3 p , vec3 n){

    vec3 v = q - dot(q - p, n) * n;
    return v;
}

// Taken from https://www.shadertoy.com/view/4ts3z2
float tri(in float x){return abs(fract(x)-.5);}
vec3 tri3(in vec3 p){return vec3( tri(p.z+tri(p.y*1.)), tri(p.z+tri(p.x*1.)), tri(p.y+tri(p.x*1.)));}


// Taken from https://www.shadertoy.com/view/4ts3z2
float triNoise3D(in vec3 p, in float spd)
{
    float z=1.4;
	float rz = 0.;
    vec3 bp = p;
	for (float i=0.; i<=3.; i++ )
	{
        vec3 dg = tri3(bp*2.);
        p += (dg+iTime*.1*spd);

        bp *= 1.8;
		z *= 1.5;
		p *= 1.2;
        //p.xz*= m2;

        rz+= (tri(p.z+tri(p.x+tri(p.y))))/z;
        bp += 0.14;
	}
	return rz;
}

float posToFloat( vec3 p ){

    float f = triNoise3D( p * .2, .1 );
    return f;

}




float udBox( vec3 p, vec3 b )
{
  return length(max(abs(p)-b,0.0));
}


float udRoundBox( vec3 p, vec3 b, float r )
{
  return length(max(abs(p)-b,0.0))-r;
}


float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float dot2( in vec3 v ) { return dot(v,v); }
float udTriangle( vec3 p, vec3 a, vec3 b, vec3 c )
{
    vec3 ba = b - a; vec3 pa = p - a;
    vec3 cb = c - b; vec3 pb = p - b;
    vec3 ac = a - c; vec3 pc = p - c;
    vec3 nor = cross( ba, ac );

    return sqrt(
    (sign(dot(cross(ba,nor),pa)) +
     sign(dot(cross(cb,nor),pb)) +
     sign(dot(cross(ac,nor),pc))<2.0)
     ?
     min( min(
     dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
     dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
     dot2(ac*clamp(dot(ac,pc)/dot2(ac),0.0,1.0)-pc) )
     :
     dot(nor,pa)*dot(nor,pa)/dot2(nor) );
}

//----
// Camera Stuffs
//----
mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

void doCamera( out vec3 camPos, out vec3 camTar, in float time, in vec2 mouse )
{
    float an = 0.3 + 10.0*mouse.x;
	camPos = vec3(5.5*sin(an),0.,5.5*cos(an));
    camTar = vec3(0.0,0.0,0.0);
}

// ROTATION FUNCTIONS TAKEN FROM
//https://www.shadertoy.com/view/XsSSzG
mat3 xrotate(float t) {
	return mat3(1.0, 0.0, 0.0,
                0.0, cos(t), -sin(t),
                0.0, sin(t), cos(t));
}

mat3 yrotate(float t) {
	return mat3(cos(t), 0.0, -sin(t),
                0.0, 1.0, 0.0,
                sin(t), 0.0, cos(t));
}

mat3 zrotate(float t) {
    return mat3(cos(t), -sin(t), 0.0,
                sin(t), cos(t), 0.0,
                0.0, 0.0, 1.0);
}


mat3 fullRotate( vec3 r ){

   return xrotate( r.x ) * yrotate( r.y ) * zrotate( r.z );

}

float rotatedBox( vec3 p , vec3 rot , vec3 size , float rad ){

    vec3 q = fullRotate( rot ) * p;
    return udRoundBox( q , size , rad );


}



// checks to see which intersection is closer
// and makes the y of the vec2 be the proper id
vec2 opU( vec2 d1, vec2 d2 ){

	return (d1.x<d2.x) ? d1 : d2;

}

//--------------------------------
// Modelling 
//--------------------------------
vec2 map( vec3 pos ){  

    float s3 = sqrt( 3. );
    pos /= 2.5;
    pos += vec3( 1. , s3 /3., 0.5 );


    vec3 a, b, c, o;

    a = vec3( 0. , 0., -10000. );
    b = vec3(  -1000000. , 0. , 10000. );
    c = vec3(  1000000. , 0. , 10000. );
    vec2 res = vec2( udTriangle( pos , a ,b , c) , 100. ); 

    float ang = PI/2.5;
    mat3 rotX = xrotate( ang  - .4 * ( 1. + sin( iTime ) ) );
    mat3 rotZ = zrotate( -.5 );


  	pos =  rotX * pos;

    // using super thin cube as plane
    vec3 size = vec3( 1.  , 1. , .01 );
   // vec3 rot = vec3( iTime * .1 , iTime * .4 , -iTime * .3 );
    vec3 rot = vec3( 0.,0.,0. );


    //res = vec2( sdSphere( pos , .03 ) , 1.0 );

    //float s3 = sqrt( 3. );




    a = vec3( 0. , 0. , 0. );
    b = vec3( 0. , s3 , 0. );
    c = vec3( -.5 , s3 / 2. , 0. );

    o = vec3( .05 , -0.05 , 0. );


     res = opU( res , vec2( udTriangle( pos , a-o ,b-o , c-o) , 1. ));

    o = vec3( -.0 , -0.1 , 0. );
    a = vec3( 0. , 0. , 0. );
    b = vec3( 1. , s3 , 0. );
    c = vec3( 0. , s3 , 0. );
    res = opU( res , vec2( udTriangle( pos , a-o ,b-o , c-o) , 2. ));


    o = vec3( -.05 , -0.05 , 0. );
    a = vec3( 0. , 0. , 0. );
    b = vec3( 1.5 , s3 / 2. , 0. );
    c = vec3( 1. , s3 , 0. );
    res = opU( res , vec2( udTriangle( pos , a-o ,b-o , c-o) , 3. ));

    o = vec3( -.1 , 0.02 , 0. );
    a = vec3( 0. , 0. , 0. );
    b = vec3( 2. ,0. , 0. );
    c = vec3( 1.5 , s3 /2. , 0. );
    res = opU( res , vec2( udTriangle( pos , a-o ,b-o , c-o) , 4. ));



   	return res;

}



vec2 calcIntersection( in vec3 ro, in vec3 rd ){


    float h =  INTERSECTION_PRECISION*2.0;
    float t = 0.0;
	float res = -1.0;
    float id = -1.;

    for( int i=0; i< NUM_OF_TRACE_STEPS ; i++ ){

        if( h < INTERSECTION_PRECISION || t > MAX_TRACE_DISTANCE ) break;
	   	vec2 m = map( ro+rd*t );
        h = m.x;
        t += h;
        id = m.y;

    }

    if( t < MAX_TRACE_DISTANCE ) res = t;
    if( t > MAX_TRACE_DISTANCE ) id =-1.0;

    return vec2( res , id );

}


#define STEPS 1
vec3 fogCube( vec3 ro , vec3 rd , vec3 n ){

    float dVal = dot( rd , n );
    float lum = 1.;
    float step = 0.;
    float val = 0.;

    vec3 col = vec3( 0. );
    float broken = 0.;
    for( int i = 0; i < STEPS; i++ ){
        vec3 p = ro + rd * .036 * abs( dVal ) * float( i );

        val += fpn( p * 20.2  );

        //val += fpn( p * 50.2  );

        //val += fpn( p * 200.2  );
    	val *= triNoise3D( p * .2 , .4 );
       // val += triNoise3D( p * .03 , .1 );

        float v =  pow(1.5-.3 * length( p - vec3( -2. , -.75 , -.5 )),2.);
        val *= 5. * v;
        val = pow( val , 2. );
        col += vec3( val ) / float( STEPS );///hsv( val , 1. , 1. ) * float( i ) / float( STEPS );
        step = float(i);

        // float blend = clamp(  ( val - .4 ) * 20.  , 0. , 1. );


       // if( val > .4 ){ broken = 1.; break; }


    }

   // float blend = clamp(  val + 1.4  , 0. , 1. );

    col *= 1. / float( STEPS );
	//col = hsv( val * .1 , 1. , 1. ) * val * .1;

    return col;


}

// Calculates the normal by taking a very small distance,
// remapping the function, and getting normal for that
vec3 calcNormal( in vec3 pos ){

	vec3 eps = vec3( 0.001, 0.0, 0.0 );
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	    map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	    map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    //-----------------------------------------------------
    // camera
    //-----------------------------------------------------

    // camera movement
    vec3 ro, ta;
    doCamera( ro, ta, iTime, m );

    // camera matrix
    mat3 camMat = calcLookAtMatrix( ro, ta, 0.0 );  // 0.0 is the camera roll

	// create view ray
	vec3 rd = normalize( camMat * vec3(p.xy,2.0) ); // 2.0 is the lens length

    vec2 res = calcIntersection( ro , rd  );


    vec3 col = vec3( 0. );

    mat3 basis = mat3(

        1. , 0. , 0. ,
        0. , 1. , 0. ,
        0. , 0. , 1.

    );



    if( res.y > -.5 ){

        vec3 pos = ro + rd * res.x;
        vec3 norm = calcNormal( pos );

        if( res.y > 10. ){
            col = vec3( .01 );
        }

        if( res.y < 10. ){

            vec3 c = hsv( res.y / 10. + .5 , 1. , 1. );
        	vec3 depthColor = fogCube( pos , rd , norm  );
        	col += c* depthColor;//lum  * vec3( 1. , .6 , 0.2);
            //col += c * .1;
        }


    }

    fragColor = vec4( col , 1. );



}
