void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv=(2.*fragCoord-iResolution.xy)/iResolution.y;
    float r = length(uv);

    // V1: Sigmoid: logistic function:
    //float x = 2.0*iResolution.y*(r-1.0);
    //float Sigmoid = 1.0 / (1.0 + exp(-x));

    // V2: Sigmoid: x / sqrt(1 + x^2):
    float x =  iResolution.y * (r - 1.0);
	float Sigmoid = 0.5*(1.0 + x / sqrt(1.0 + x*x));

    fragColor = vec4(Sigmoid);
}
