void mainImage( out vec4 color, in vec2 pixelCoords )
{
	float x = pixelCoords.x;
    float y = pixelCoords.y;

    float circleX = 830.0 * cos(iTime) + 350.0;
    float circleY = 210.0;

    float deltaX = x - circleX;
    float deltaY = y- circleY;
    float distance = sqrt(deltaX*deltaX + deltaY * deltaY );


    //yeux


   float eyeX1 = circleX - 30.0;
   float eyeY1 = circleY;

   float dEyeX1 = x - eyeX1;
   float dEyeY1 = y - eyeY1;
   float distanceEye1 = sqrt (dEyeX1*dEyeX1 + dEyeY1*dEyeY1*3.0);

    float eyeX2 = circleX + 30.0;
    float eyeY2 = circleY;

    float dEyeX2 = x - eyeX2;
    float dEyeY2 = y - eyeY2;
    float distanceEye2 = sqrt (dEyeX2*dEyeX2 + dEyeY2*dEyeY2*3.0);

    //bouche

    float boucheX = circleX;
    float boucheY = circleY - 40.0;

    float dBoucheX = x - boucheX;
    float dBoucheY = y - boucheY;
    float distanceBouche = sqrt (dBoucheX*dBoucheX + dBoucheY*dBoucheY);

    //creuxBouche

   float creuxBoucheX = circleX;
    float creuxBoucheY = circleY - 27.0;

    float dCreuxBoucheX = x - creuxBoucheX;
    float dCreuxBoucheY = y - creuxBoucheY;
    float distanceCreuxBouche = sqrt (dCreuxBoucheX*dCreuxBoucheX + dCreuxBoucheY*dCreuxBoucheY*2.0);


    //creux

    float creuxX = circleX;
    float creuxY = circleY + 124.0;

    float dCreuxX = x - creuxX;
    float dCreuxY = y - creuxY;
    float distanceCreux = sqrt (dCreuxX*dCreuxX + dCreuxY*dCreuxY);

    //flamme

    float fireX = circleX;
    float fireY = circleY + 76.0;
    float dFireX = x - fireX;
    float dFireY = y - fireY;
    float distanceFire = sqrt(dFireX*dFireX * 4.0 + dFireY*dFireY);

    //f blanche

    float fire2X = circleX;
    float fire2Y = circleY + 71.0;
    float dFire2X = x - fire2X;
    float dFire2Y = y - fire2Y;
    float distanceFire2 = sqrt(dFire2X*dFire2X * 4.0 + dFire2Y*dFire2Y);




    if (distanceEye1 < 11.0) {
    	color = vec4(0.0, 0.0, 0.0, 1.0);
    }
  	else if (distanceEye2 < 11.0) {
    	color = vec4(0.0, 0.0, 0.0, 1.0);
    }
    else if (distanceFire2 < 15.0) {
    	color = vec4(1.0, 1.0, 1.0, 1.0);
    }
    else if (distanceFire < 21.0) {
    	color = vec4(1.0, 1.0, 0.0, 1.0);
    }
    else if (distanceCreuxBouche < 32.0) {
    	color = vec4(1.0, 0.3, 0.1, 1.0);
    }
    else if (distanceBouche < 32.0) {
        color = vec4(0.0, 0.0, 0.0, 1.0);
    }
    else if (distanceCreux < 80.0){
        color = vec4(0.0, 0.0, 0.0, 1.0);
    }
    else if (distance < 80.0){
        color = vec4(1.0, 0.3, 0.1, 1.0);
    }

    else {
        color = vec4(0.0, 0.0, 0.0, 1.0);
    }

}
