void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 col = texture( iChannel0, uv ).xyz;

    float k = col.x;
    float j = col.y;

	float inc = ((uv.x + uv.y) / 100.0) * 0.99 + 0.01;

    if (j == 0.0) {
	    k += inc;
    }
    else {
	    k -= inc;
    }

    if (k >= 1.0)
        j = 1.0;

    if (k <= 0.0)
        j = 0.0;

    fragColor = vec4(k, j, 0.0, 1.0);
}
