vec4 circle(vec2 uv, vec2 pos, float rad, vec3 color){
    float d = length(pos - uv) - rad;
    float t = clamp(d, 0.0, 1.0);
    return vec4(color, 1.0-t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;
    float speed = 4.0;
	vec4 backGround = vec4(0.9, 0.8, 0.7, 1.0);
    float radius = iResolution.y * 0.1;
    float turnRadius = 100.0;
    vec2 center = vec2(0.5 * iResolution.x + (turnRadius * cos(speed * iTime)), 
                       0.5 * iResolution.y +(turnRadius * sin(speed * iTime)));

    vec3 shapeColor = vec3(0.3, 0.73, 0.14);
    vec4 shape = circle(uv, center, radius, shapeColor);
    fragColor = mix(backGround, shape, shape.a);
}
