#define MAXIT 100

mat2 rot2D(float a){
   float c =cos(a);
   float s =sin(a);

   return mat2(c,-s,s,c);
}
vec2 IPower2(vec2 a,vec2 b){

  return vec2(a.x*b.x-a.y*b.y,a.x*b.y+a.y*b.x);
}


void Julia(vec2 uv,inout vec3 col){
    vec2 z =uv;
    float t1 =iTime*0.05;

    vec2 c =vec2(-0.044*abs(cos(t1)),0.69*abs(sin(t1)));
    float v =0.;

    for(int i=0;i<MAXIT;i++){
        z = IPower2(z,z)+c; 
        if(dot(z,z)>2.0){
            v=abs(sin(iTime*4.+20.*dot(z,uv)));

            break;
        }

    }

    float r =v*0.8;
    float g =cos(length(z)-v*0.5)*clamp(dot(z,z),0.5,2.);
    float b =0.1;

    float arc = atan(z.y,z.x+1e-15);

    float v1 = 0.4/(length(uv-z));

    v1=pow(v1,2.5);

    col+=vec3(r,g,b)*vec3(v1);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy-vec2(0.5);
    float as= iResolution.x/iResolution.y;
    uv.x*=as;

    uv*=rot2D(iTime*0.05);
    float c =1.9*abs(cos(iTime*0.05));
    uv*=c;
    uv.x-=0.4*sin(c);
    vec3 col =vec3(0);

    Julia(uv,col);

    col = sqrt(col);

    fragColor = vec4(col,1.0);
}
